mapper.update:
	
	 update t_channel_data 
	SET dealStatus = CASE id 
		
			WHEN #{item.id} THEN #{item.dealStatus} 
		
        END, 
    dealDetail = CASE id 
       
			WHEN #{item.id} THEN #{item.dealDetail} 
		
        END,
        dealDate = #{nowDate},dealManagerId= #{managerId}
	where id in
    
   	 	#{item.id}

mapper.select:
select c.id as id,c.systemId as systemId ,c.channel as channel,c.dateTime as dateTime, c.type as type,c.value as value,c.systemType as systemType,c.dealStatus as dealStatus, c.dealDetail as dealDetail,c.deviceAddress as deviceAddress,c.deviceId as deviceId,hd.WarningMin as WarningMin, hd.WarningMax as WarningMax ,hd.HiddenMin as HiddenMin, hd.HiddenMax as HiddenMax, hd.name as name from t_channel_data c LEFT JOIN t_passageway p on c.channel=p.channel and c.deviceId =p.deviceId LEFT join t_hidden_danger hd on hd.id = p.hiddenDangerId
order by ${example.orderByClause}
limit #{offset},#{limit}
